import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ResponderHistory() {
    const [histories, setHistories] = useState([]);

    const fetchHistoryByStatus = async (status) => {
        const token = localStorage.getItem('responderToken');
        const response = await axios.get(`/api/dispatch-history/search?status=${status}`, { headers: { authorization: `Bearer ${token}` }});
        return response.data;
    };

    useEffect(() => {
        const fetchAllHistories = async () => {
            try {
                const statuses = ['pending', 'dispatched', 'arrived', 'finished'];
                let allHistories = [];
                for (const status of statuses) {
                    const historiesByStatus = await fetchHistoryByStatus(status);
                    allHistories = allHistories.concat(historiesByStatus);
                }
                setHistories(allHistories);
            } catch (error) {
                console.error('Error fetching histories:', error);
            }
        };
        fetchAllHistories();
    }, []);

    const handleAccept = async (historyId) => {
        try {
            const token = localStorage.getItem('responderToken');
            await axios.post(`/api/responder/accept/${historyId}`, {}, {headers: { authorization: `Bearer ${token}` }});
            setHistories(histories.filter(history => history.id !== historyId)); // Remove accepted history from the list
        } catch (error) {
            console.error('Error accepting history item:', error);
        }
    };

    const renderHistorySection = (status) => {
        return (
            <div>
                <h3>{status.charAt(0).toUpperCase() + status.slice(1)} Histories</h3>
                {histories.filter(history => history.status === status).map(history => (
                    <div key={history.id} className="history-item">
                        {/* Display history details */}
                        {/* ... */}
                        {status === 'pending' && (
                            <button onClick={() => handleAccept(history.id)}>Accept</button>
                        )}
                    </div>
                ))}
            </div>
        );
    };

    return (
        <div>
            <h2>Responder History</h2>
            {['pending', 'dispatched', 'arrived', 'finished'].map(status => renderHistorySection(status))}
        </div>
    );
}

export default ResponderHistory;
